INSERT INTO `bbs_tag` VALUES (39, '微信开发者工具', NULL, '为了帮助开发者简单和高效地开发和调试微信小程序，微信在原有的公众号网页调试工具的基础上，推出了全新的微信开发者工具，集成了公众号网页调试和小程序调试两种开发模式。\n\n1.使用公众号网页调试，开发者可以调试微信网页授权和微信JS-SDK\n2.使用小程序调试，开发者可以完成小程序的 API 和页面的开发调试、代码查看和编辑、小程序预览和发布等功能。', NULL, NULL, 1, 1, '2019-04-23 20:32:18', NULL, NULL, NULL, NULL, 1);
