INSERT INTO `bbs_tag` VALUES (97, 'ruby-on-rails', NULL, 'Ruby on Rails，简称Rails，是一个使用Ruby语言写的开源Web应用框架，它是严格按照MVC结构开发的。它努力使自身保持简单，来使实际的应用开发时的代码更少，使用最少的配置。  \nRails的设计原则包括“不做重复的事”（Don\'t Repeat Yourself）和“惯例优于设置”（Convention Over Configuration）', NULL, NULL, 1, 1, '2019-04-23 20:58:42', NULL, NULL, NULL, NULL, 1);
