INSERT INTO `bbs_tag` VALUES (125, 'java-ee', NULL, 'Java EE（Java Platform，Enterprise Edition）是sun公司推出的企业级应用程序版本。这个版本以前称为 J2EE。能够为我们帮助开发和部署可移植、健壮、可伸缩且安全的服务器端 Java 应用程序。Java EE 是在 Java SE 的基础上构建的，它提供 Web 服务、组件模型、管理和通信 API，可以用来实现企业级的面向服务体系结构（service-oriented architecture，SOA）和 Web 2.0 应用程序。', NULL, NULL, 1, 1, '2019-04-23 21:10:59', NULL, NULL, NULL, NULL, 1);
