INSERT INTO `bbs_tag` VALUES (152, 'centos', NULL, 'CentOS（Community ENTerprise Operating System）是Linux发行版之一，它是来自于Red Hat Enterprise Linux依照开放源代码规定释出的源代码所编译而成。由于出自同样的源代码，因此有些要求高度稳定性的服务器以CentOS替代商业版的Red Hat Enterprise Linux使用。两者的不同，在于CentOS并不包含封闭源代码软件。', NULL, NULL, 1, 1, '2019-04-23 21:19:53', NULL, NULL, NULL, NULL, 1);
INSERT INTO `bbs_tag` VALUES (153, '负载均衡', NULL, '负载均衡是一种计算机网络技术，用来在多个计算机(计算机集群)、网络连接、CPU、磁盘驱动器或其他资源中分配负载，以达到优化资源使用、最大化吞吐率、最小化响应时间、同时避免过载的目的。  \n使用带有负载均衡的多个服务组件，取代单一的组件，可以通过冗余提高可靠性。负载均衡服务通常是由专用软件和硬件来完成。', NULL, NULL, 1, 1, '2019-04-23 21:20:13', NULL, NULL, NULL, NULL, 1);
INSERT INTO `bbs_tag` VALUES (154, '缓存', NULL, '现在高速缓存的概念已被扩充，不仅在CPU和主内存之间有Cache而且在内存和硬盘之间也有Cache（磁盘高速缓存），乃至在硬盘与网络之间也有某种意义上的Cache ── 凡是位于速度相差较大的两种硬件之间的，用于协调两者数据传输速度差异的结构，均可称之为Cache。', NULL, NULL, 1, 1, '2019-04-23 21:20:32', NULL, NULL, NULL, NULL, 1);
